# valor inicialx = 0;tolerancy = 0.000000000001;maxIter = 500;x_ant = x;printf("%4s,  ", "Iter");printf("%20s,  ", "x");printf("%20s,  ", "f(x)");printf("%20s,  ", "f'(x)");printf("%20s,  ", "f''(x)");printf("%20s,\n", "Error");for i=1:maxIter+1   x_ant = x;   x = x_ant - calcularFx(x_ant) / (calcularFxd(x_ant) - calcularFx(x_ant)/(2*calcularFxd(x_ant)) .* calcularFx2d(x_ant));   printf("%4i,  ", i);   printf("%20.15f,  ", x);   printf("%20.15f,  ", calcularFx(x));   printf("%20.15f,  ", calcularFxd(x));   printf("%20.15f,  ", calcularFx2d(x));   error = abs((abs(x) - abs(x_ant))/abs(x)).*100;                 printf("%20.15f,\n", error);   if( error < tolerancy )      printf("\nResultado encontrado en la iteracion: %i\n", i);      printf("El valor es: %.15f\n", x);      printf("El error es: %.15f\n", error);      break;   endifendfor