# Ecuaci√≥n x - e^-x = 0# Despeje x = e^-x# Derivada x' = -e^-x# gx = (x^2 - e^x)/5tolerancy = 0.000000000001;x0 = 3;for i=0:100  #gx = sqrt(11 .* x0 - 7);  #gx = e^-x0;  gx = (x0**2 - (e**x0))/5;  #gx = x0**x0 + x0 - 100;  #gx = (sin(x0))**(1/3);  error = abs((abs(gx) - abs(x0))/abs(gx)).*100;  printf("Iteracion: %i\n", i);  printf("Valor de gx: %.15f\n", gx);  printf("El error es: %.15f\n\n", error);  if( error < tolerancy )    printf("\nResultado encontrado en la iteracion: %i\n", i);    printf("El valor es: %.15f\n", gx);    printf("El error es: %.15f\n", error);    break;  endif  x0 = gx;endfor